import React, { useState } from 'react';
import axios from 'axios';

import { TextInput, ImageDisplay, SubmitButton } from './components'

interface ImageData {
  imageUrl: string;
}

const App: React.FC = () => {
  const [inputText, setInputText] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  const handleInputChange = (value: string) => {
    setInputText(value);
  };

  const handleSubmit = async () => {
    try {
      console.log(`${process.env.API_URL}/images?query=${inputText}`);
      setImageUrl('data:image/png;base64,');
      const response = await axios.get<ImageData>(
        `${process.env.API_URL}/images?query=${inputText}`
      );
      // setImageUrl(response.data.imageUrl);
    } catch (error) {
      console.error('Error fetching the painting result:', error);
    }
  };

  return (
    <div>
      <h1>Image Search</h1>
      <TextInput onInputChange={handleInputChange} />
      <SubmitButton onSubmit={handleSubmit} />
      {imageUrl && <ImageDisplay imageUrl={imageUrl} />}
    </div>
  );
};

export default App;
